import React from "react";
import ScrollToTop from "react-scroll-up";
import { FiChevronUp } from "react-icons/fi";
import PageHelmet from "../components/Helmet/Helmet";
import Breadcrumb from "../components/BreadCrumb/Breadcrumb";
import Header from "../components/Header/Header";
import FooterTwo from "../components/Footer/FooterTwo";

const NewsPolicy = () => {
  return (
    <>
      <PageHelmet pageTitle="Columns" />

      {/* Start Header Area  */}
      <Header
        headertransparent="header--transparent"
        colorblack="color--black"
        logoname="logo.png"
      />
      {/* End Header Area  */}

      {/* Start Breadcrump Area */}
      <Breadcrumb title={"Нийтлэлийн бодлого"} />
      {/* End Breadcrump Area */}

      {/* Start Page Wrapper  */}
      <main className="page-wrapper">
        {/* Start Columns Area  */}
        <div className="rn-columns-area ptb--120 bg_color--5">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <div className="single-column">
                  <p>
                    Media Lab цахим товхимол нь эрхлэн гаргаж буй агуулга,
                    хүлээн авагчдадаа хүргэж буй мэдээллүүдийг дэлхийн стандарт,
                    хэвлэл мэдээллийн байгууллагын ёс зүйн зарчимд нийцүүлэн
                    ажиллана. Бид эрхлэн гаргаж буй агуулгадаа дараах бодлогыг
                    баримтлан ажиллана.
                  </p>
                  <h3 className="tilte">НЭГ. НИЙТЛЭЛИЙН ЕРӨНХИЙ БОДЛОГО</h3>
                  <h5 className="tilte"> 1. Ерөнхий бодлого</h5>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
      {/* End Page Wrapper  */}

      {/* Start Back To Top */}
      <div className="backto-top">
        <ScrollToTop showUnder={160}>
          <FiChevronUp />
        </ScrollToTop>
      </div>
      {/* End Back To Top */}

      {/* Start Footer Area  */}
      <FooterTwo />
      {/* End Footer Area  */}
    </>
  );
};

export default NewsPolicy;
